perm([],[]).perm([X|T],PL) :- perm(T,PT),insert(X,PT,PL).insert(X,[],[X]).insert(X,[H|T],[X,H|T]).insert(X,[H|T],[H|IT]) :- insert(X,T,IT).slowsort(L,SL) :-	perm(L,SL),	ordered(SL).		ordered([]).ordered([_X]).ordered([X,Y|T]) :- X =< Y, ordered([Y|T]).not_ordered([X,Y|T]) :- X >Y.not_ordered([X,Y|T]) :- X =< Y, not_ordered([Y|T]).list([]).list([H|T]) :- list(T).list1([]).list1([1|T]) :- list1(T).