:- mode fib(i,o).fib(0,1).fib(s(0),1).fib(s(s(X)),F) :- fib(s(X),F1),fib(X,F2),F is F1 + F2./* :- table plus/3. */:- mode plus(i,i,o).plus(0,X,X).plus(s(X),Y,s(Z)) :- plus(X,Y,Z)./* :- table fibs/2. */:- mode fibs(i,o). fibs(0,s(0)).fibs(s(0),s(0)).fibs(s(s(X)),F) :- fibs(s(X),F1), fibs(X,F2), plus(F1,F2,F)./*test(X,Z) :- X is Z + 0.gen(0,0).gen(Nr,s(X)):- Nr>0, N1 is Nr-1, gen(N1,X).*//* By adding the msv with functionality, Ecce can produce : *//* Specialised Predicates: fib__1(X1,X2) :- fib(X1,X2).fib_conj__2(X1,X2,X3) :- fib(s(X1),X2), fib(X1,X3).*//*fib(X1,X2) :-     fib__1(X1,X2).fib__1(0,1).fib__1(s(0),1).fib__1(s(s(X1)),X2) :-     fib_conj__2(X1,X3,X4),     X2 is '+'(X3,X4).fib_conj__2(0,1,1).fib_conj__2(s(X1),X2,X3) :-     fib_conj__2(X1,X3,X4),     X2 is '+'(X3,X4). */