sameleaves(leaf(L),leaf(L)).sameleaves(tree(T1,T2),tree(S1,S2)) :-  getleaf(T1,T2,L,T),  getleaf(S1,S2,L,S),  sameleaves(S,T).getleaf(leaf(A),C,A,C).getleaf(tree(A,B),C,L,O) :- getleaf(A,tree(B,C),L,O).frontier(leaf(X),[X]).frontier(nil,[]).frontier(tree(L,R,Lbl),Res) :-   frontier(L,L1),   frontier(R,R1),   append(L1,R1,Res).   frontier1(leaf(X),[X]).frontier1(nil,[]).frontier1(tree(L,R,Lbl),Res) :-   frontier(L,L1),   frontier(R,R1),   append(L1,R1,Res).   append([],X,X).append([H|X],Y,[H|Z]) :- append(X,Y,Z).