:- dynamic blockpair2o/0, causesPair/2, actionPair/2, sameMultiSet/2. blockpair2o:-	causesPair(o(on(s(nul),nul), o(ta(nul), o(cl(s(nul)),em))),		   o(on(s(s(nul)),s(nul)), o(on(s(nul),nul), o(ta(nul),						      o(cl(s(s(nul))),em))))).causesPair(I1, I2):-	sameMultiSet(I1, I2).causesPair(I, G):-          actionPair(C, E),        sameMultiSet(o(C, Z), I),        causesPair(o(E, Z), G).actionPair(ho(V), o(ta(V),o(cl(V),em))).actionPair(o(cl(V),o(ta(V),em)), ho(V)).actionPair(o(ho(V),cl(W)), o(on(V,W),o(cl(V),em))).actionPair(o(cl(V),o(on(V,W),em)), o(ho(V),cl(W))).actionPair(o(on(V,W),o(cl(V),em)),	   o(on(s(s(V)),s(V)), o(on(s(V),V), o(on(V,W), o(cl(s(s(V))),em))))).actionPair(o(on(s(s(V)),s(V)), o(on(s(V),V), o(on(V,W), o(cl(s(s(V))),em)))),	   o(on(V,W), o(cl(V),em))).sameMultiSet(X, X).sameMultiSet(o(X, Y), o(X, Z)):-	sameMultiSet(Y, Z).sameMultiSet(o(o(X, Y), Z), U):- 	sameMultiSet(o(X, o(Y, Z)), U).sameMultiSet(U, o(o(X, Y), Z)):-	sameMultiSet(U, o(X, o(Y, Z))).sameMultiSet(o(emptyMultiSet, X), Y):-	sameMultiSet(X, Y).sameMultiSet(X, o(emptyMultiSet, Y)):-	sameMultiSet(X, Y).sameMultiSet(o(X, Y), Z):-	sameMultiSet(o(Y, X), Z).