:- dynamic blockpair3o/0, causesPair/3, actionPair/3, sameMultiSet/2. blockpair3o:-	causesPair(o(on(s(nul),nul), o(ta(nul), o(cl(s(nul)),em))), _Plan,		   o(on(s(s(nul)),s(nul)), o(on(s(nul),nul), o(ta(nul),						       o(cl(s(s(nul))),em)))))causesPair(I1, void, I2):-	sameMultiSet(I1, I2).causesPair(I, plan(A, P), G):-          actionPair(C, A, E),        sameMultiSet(o(C, Z), I),        causesPair(o(E, Z), P, G). actionPair(ho(V),put_down(V),o(ta(V),o(cl(V),em))).                            actionPair(o(cl(V),o(ta(V),em)),pick_up(V),ho(V)).                             actionPair(o(ho(V),cl(W)),stack(V,W),o(on(V,W),o(cl(V),em))).actionPair(o(cl(V),o(on(V,W),em)),unstack(V),o(ho(V),cl(W))).actionPair(o(on(V,W),o(cl(V),em)),add_two,                                     	   o(on(s(s(V)),s(V)), o(on(s(V),V), o(on(V,W), o(cl(s(s(V))),em))))).actionPair(o(on(s(s(V)),s(V)), o(on(s(V),V), o(on(V,W), o(cl(s(s(V))),em)))),           delete_two,	   o(on(V,W), o(cl(V),em))).sameMultiSet(X, X).sameMultiSet(o(X, Y), o(X, Z)):-	sameMultiSet(Y, Z).sameMultiSet(o(o(X, Y), Z), U):- 	sameMultiSet(o(X, o(Y, Z)), U).sameMultiSet(U, o(o(X, Y), Z)):-	sameMultiSet(U, o(X, o(Y, Z))).sameMultiSet(o(emptyMultiSet, X), Y):-	sameMultiSet(X, Y).sameMultiSet(X, o(emptyMultiSet, Y)):-	sameMultiSet(X, Y).sameMultiSet(o(X, Y), Z):-	sameMultiSet(o(Y, X), Z).