:- dynamic blockpairl/0, causesPairl/3, actionPairl/3,	   app/3, m_subset/3, delete/3. blockpairl:-	causesPairl([on(s(nul),nul),ta(nul),cl(s(nul)),em], _Plan, Sequence),	m_subset([on(s(s(nul)),s(nul)),on(s(nul),nul),ta(nul),cl(s(s(nul))),em],		 Sequence, []).causesPairl(I,void,I).                   causesPairl(I,plan(A,P),G) :-	actionPairl(C,A,E), 	m_subset(C,I,Z),	app(E,Z,S),	causesPairl(S,P,G).actionPairl([ho(V)],put_down(V),[ta(V),cl(V),em]).                            actionPairl([cl(V),ta(V),em],pick_up(V),[ho(V)]).                             actionPairl([ho(V),cl(W)],stack(V,W),[on(V,W),cl(V),em]).                     actionPairl([cl(V),on(V,W),em],unstack(V),[ho(V),cl(W)]).                     actionPairl([on(V,W),cl(V),em],add_two,                                     	    [on(s(s(V)),s(V)),on(s(V),V),on(V,W),cl(s(s(V))),em]).             actionPairl([on(s(s(V)),s(V)),on(s(V),V),on(V,W),cl(s(s(V))),em],delete_two,	    [on(V,W),cl(V),em]).                                             m_subset([], L, L).m_subset([H|T], L1, L2):-        delete(H, L1, L3),        m_subset(T, L3, L2).delete(M, [M|T], T).delete(M, [H|T], [H|L]):-        delete(M, T, L).app([], X, X).app([X|Y], Z, [X|W]):-        app(Y, Z, W).