:- dynamic petri/0, petri/1, tr/3, trace/3, trans/3.petri:-         trace(Tr,[X,s(0),0,0,0],[_,_,s(s(_)),_,_]).        trace([],State,State).trace([Action|As],InState,OutState) :-        trans(Action,InState,S1),        trace(As,S1,OutState).trans(enter_cs,[s(X),s(Sema),CritSec,Y,C],               [X,Sema,s(CritSec),Y,C]).trans(exit_cs, [X,Sema,s(CritSec),Y,C],               [X,s(Sema),CritSec,s(Y),C]).trans(restart, [X,Sema,CritSec,s(Y),ResetCtr],               [s(X),Sema,CritSec,Y,s(ResetCtr)]).