nrev([],[]).nrev([X|Xs],Y) :- nrev(Xs,Z),app(Z,[X],Y).nrev2([],[]).nrev2([X|Xs],Y) :- app(Z,[X],Y),nrev2(Xs,Z).app([],L,L).app([H|X],Y,[H|Z]) :- app(X,Y,Z).lemma_app_nil(X,Y) :- app(X,[],Y).lemma_assoc(X,Y,Z,R1,R2) :- app(X,Y,R), app(R,Z,R1),                      app(Y,Z,V), app(X,V,R2).                      lemma_rev_app(X,Y,R1,R2) :- app(X,Y,V), rev(V,R1),                            rev(Y,YR),rev(X,XR), app(YR,XR,R2).                            theorem_rev_rev(X,R) :- nrev(X,Z), nrev(Z,R).