:- dynamic blockzero/0, causeszero/3, actionzero/3, sameMultiset/2.blockzero:-	causesZero(o(o(on(s(s(nul)), s(nul)), cl(s(s(nul)))), em), _P,		   o(on(s(nul), nul), _Z)).causesZero(I1, void, I2):-	sameMultiSet(I1, I2).causesZero(I, plan(A, P), G):-          actionZero(C, A, E),        sameMultiSet(o(C, Z), I),        causesZero(o(E, Z), P, G).actionZero(ho(V), put_down(V), o(ta(V), o(cl(V), em))).actionZero(o(cl(V), o(ta(V), em)), pick_up(V), ho(V)).actionZero(o(ho(V), cl(W)), stack(V, W), o(on(V,W), o(cl(V), em))).actionZero(o(cl(V), o(on(V, W), em)), unstack(V), o(ho(V), cl(W))).actionZero(o(on(X, Y), o(cl(X), em)), generate_block,	   o(on(s(X), X), o(on(X, Y), o(cl(s(X)), em)))).sameMultiSet(X, X).sameMultiSet(o(X, Y), o(X, Z)):-	sameMultiSet(Y, Z).sameMultiSet(o(o(X, Y), Z), U):- 	sameMultiSet(o(X, o(Y, Z)), U).sameMultiSet(U, o(o(X, Y), Z)):-	sameMultiSet(U, o(X, o(Y, Z))).sameMultiSet(o(emptyMultiSet, X), Y):-	sameMultiSet(X, Y).sameMultiSet(X, o(emptyMultiSet, Y)):-	sameMultiSet(X, Y).sameMultiSet(o(X, Y), Z):-	sameMultiSet(o(Y, X), Z).